import React from 'react';
import { poseImages } from '../../utils/pose_images';

export default function DropDown({ poseList, currentPose, setCurrentPose }) {
    return (
        <div className="relative inline-block text-left">
            <button 
                className="bg-blue-500 text-white px-4 py-2 rounded-md shadow-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400"
                type="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
            >
                {currentPose}
            </button>

            <ul className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-md ring-1 ring-black ring-opacity-5 focus:outline-none">
                {poseList.map((pose) => (
                    <li 
                        key={pose} 
                        onClick={() => setCurrentPose(pose)} 
                        className="cursor-pointer hover:bg-gray-100 px-4 py-2"
                    >
                        <div className="flex items-center space-x-2">
                            <p className="text-sm font-medium text-gray-900">{pose}</p>
                            <img 
                                src={poseImages[pose]} 
                                alt={pose} 
                                className="w-8 h-8 rounded-full object-cover"
                            />
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}
